mapper.select:
SELECT ADD_META_TEMPLATE_SET_UUID AS addMetaTemplateSetUuid, ENTITY_TYPE AS entityType, SET_CODE AS setCode, SET_NAME AS setName, DESCRIPTION AS description, NOTES AS notes, USE_YN AS useYN, DEFAULT_YN AS defaultYN,
AS insertUuid,
INSERT_DATE AS insertDate,
AS updateUuid,
UPDATE_DATE AS updateDate FROM AD_CON_META_SETUP WHERE 1=1
AND ENTITY_TYPE = #{entityType}
AND SET_NAME = #{setName}
AND USE_YN = #{useYN}
ORDER BY ENTITY_TYPE ASC
