mapper.select:
SELECT A.CLASSIFY_RECORDS_UUID as classifyRecordsUuid, A.STATUS_UUID as statusUuid, A.CHOICE_YN as choiceYn, A.CLASS_UUID as classUuid, B.AGGREGATION_CODE as aggregationCode, B.TITLE as title, A.AGGREGATION_UUID as aggregationUuid, A.ITEM_UUID as itemUuid, B.TYPE_UUID as typeUuid, A.DESCRIPTION as description, A.NOTES as notes, to_char(A.CLASSIFIED_DATE, 'YYYY-MM-DD') as classifiedDate, SUBSTR(SYS_CONNECT_BY_PATH(A.ROWID, '.'), 2 , length(SYS_CONNECT_BY_PATH(A.ROWID, '.'))) as orderKey1, as insertUuid, A.INSERT_DATE as insertDate, as updateUuid, A.UPDATE_DATE as updateDate FROM (SELECT * FROM CL_CLASSIFY_RECORDS_RESULT
CLASS_UUID = #{classUuid}
) A, ( SELECT DISTINCT(AGGREGATION_UUID), PARENT_AGGREGATION_UUID, TITLE, AGGREGATION_CODE, TYPE_UUID FROM RC_AGGREGATION START WITH AGGREGATION_UUID IN ( SELECT AGGREGATION_UUID FROM CL_CLASSIFY_RECORDS_RESULT
CLASS_UUID = #{classUuid}
) CONNECT BY PRIOR PARENT_AGGREGATION_UUID = AGGREGATION_UUID) B
A.AGGREGATION_UUID(+) = B.AGGREGATION_UUID
AND A.CLASS_UUID = #{classUuid}
START WITH A.CHOICE_YN = 'Y' CONNECT BY PRIOR B.AGGREGATION_UUID = B.PARENT_AGGREGATION_UUID
