mapper.select:
SELECT Z1.AGGREGATION_UUID as uuid , Z1.PARENT_AGGREGATION_UUID as parentUuid , Z1.TITLE as name , LOWER(FC_AD_CODE_NM(Z1.TYPE_UUID)) as nodeType , (SELECT LEVEL_NO FROM RC_LEVEL WHERE LEVEL_UUID = Z1.LEVEL_UUID) as "level" , ( (SELECT COUNT(*) FROM RC_AGGREGATION WHERE PARENT_AGGREGATION_UUID = Z1.AGGREGATION_UUID) +(SELECT COUNT(*) FROM RC_ITEM WHERE AGGREGATION_UUID = Z1.AGGREGATION_UUID) )as childCnt, LOWER(FC_AD_CODE_NM(Z1.PUBLISHED_STATUS_UUID)) as publishedStatus FROM RC_AGGREGATION Z1
LOWER(FC_AD_CODE_NM(Z1.TYPE_UUID)) = LOWER ('normal')
PARENT_AGGREGATION_UUID = #{uuid}

mapper.sql:
SELECT AGGREGATION_UUID as uuid , 1 as orderNo ,(SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.PUBLISHED_STATUS_UUID) as publishedStatus , AGGREGATION_CODE as code , TITLE as title , (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.TYPE_UUID) as "type" , (SELECT TO_CHAR(LEVEL_NO) FROM RC_LEVEL WHERE LEVEL_UUID = Z1.LEVEL_UUID) as "level" , AUTHOR as author , DESCRIPTION_START_DATE as descStrDate , DESCRIPTION_END_DATE as descEdDate , DESCRIPTION as description , NOTES as notes , ( (SELECT COUNT(*) FROM RC_AGGREGATION WHERE PARENT_AGGREGATION_UUID = Z1.AGGREGATION_UUID) +(SELECT COUNT(*) FROM RC_ITEM WHERE AGGREGATION_UUID = Z1.AGGREGATION_UUID) )as childCnt FROM RC_AGGREGATION Z1
WHERE PARENT_AGGREGATION_UUID IS NULL
WHERE PARENT_AGGREGATION_UUID = #{uuid}
UNION ALL SELECT ITEM_UUID as uuid , 2 as orderNo ,(SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.PUBLISHED_STATUS_UUID) as publishedStatus , ITEM_CODE as code , TITLE as title , (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.TYPE_UUID) as "type" , '' as "level" , AUTHOR as author , DESCRIPTION_START_DATE as descStrDate , DESCRIPTION_END_DATE as descEdDate , DESCRIPTION as description , NOTES as notes , 0 as childCnt FROM RC_ITEM Z1 WHERE AGGREGATION_UUID = #{uuid}
UNION ALL SELECT Z3.AGGREGATION_UUID as uuid , 1 as orderNo ,(SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z3.PUBLISHED_STATUS_UUID) as publishedStatus , Z3.AGGREGATION_CODE as code , Z3.TITLE as title , (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z3.TYPE_UUID) as "type" , (SELECT TO_CHAR(LEVEL_NO) FROM RC_LEVEL WHERE LEVEL_UUID = Z3.LEVEL_UUID) as "level" , Z3.AUTHOR as author , Z3.DESCRIPTION_START_DATE as descStrDate , Z3.DESCRIPTION_END_DATE as descEdDate , Z3.DESCRIPTION as description , Z3.NOTES as notes , ( (SELECT COUNT(*) FROM RC_AGGREGATION WHERE PARENT_AGGREGATION_UUID = Z3.AGGREGATION_UUID) +(SELECT COUNT(*) FROM RC_ITEM WHERE AGGREGATION_UUID = Z3.AGGREGATION_UUID) )as childCnt FROM RC_AGGREGATION Z3, RC_RECORD_REFERENCE Z4 WHERE Z4.VIRTUAL_AGGREGATION_UUID = #{uuid} AND Z4.AGGREGATION_UUID = Z3.AGGREGATION_UUID
UNION ALL SELECT Z5.ITEM_UUID as uuid , 2 as orderNo ,(SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z5.PUBLISHED_STATUS_UUID) as publishedStatus , Z5.ITEM_CODE as code , Z5.TITLE as title , (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z5.TYPE_UUID) as "type" , '' as "level" , Z5.AUTHOR as author , Z5.DESCRIPTION_START_DATE as descStrDate , Z5.DESCRIPTION_END_DATE as descEdDate , Z5.DESCRIPTION as description , Z5.NOTES as notes , 0 as childCnt FROM RC_ITEM Z5, RC_RECORD_REFERENCE Z6 WHERE Z6.VIRTUAL_AGGREGATION_UUID = #{uuid} AND Z6.ITEM_UUID = Z5.ITEM_UUID

mapper.select:
SELECT AGGREGATION_UUID as uuid ,(SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.PUBLISHED_STATUS_UUID) as publishedStatus , AGGREGATION_CODE as code , TITLE as title , (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.TYPE_UUID) as "type" , (SELECT LEVEL_NO FROM RC_LEVEL WHERE LEVEL_UUID = Z1.LEVEL_UUID) as "level" , AUTHOR as author , DESCRIPTION_START_DATE as descStrDate , DESCRIPTION_END_DATE as descEdDate , DESCRIPTION as description , NOTES as notes , ( (SELECT COUNT(*) FROM RC_AGGREGATION WHERE PARENT_AGGREGATION_UUID = Z1.AGGREGATION_UUID) +(SELECT COUNT(*) FROM RC_ITEM WHERE AGGREGATION_UUID = Z1.AGGREGATION_UUID) )as childCnt FROM RC_AGGREGATION Z1
WHERE PARENT_AGGREGATION_UUID IS NULL
WHERE PARENT_AGGREGATION_UUID = #{uuid}

choose.when:
UPDATE
    RC_ITEM
SET
  AGGREGATION_UUID = #{parentUuid}
WHERE
  ITEM_UUID = #{uuid}

choose.otherwise:
UPDATE
  RC_AGGREGATION
SET
  PARENT_AGGREGATION_UUID = #{parentUuid}
WHERE
  AGGREGATION_UUID = #{uuid}

mapper.delete:
{call
  BEGIN

  DELETE FROM RC_COMPONENT_CON WHERE COMPONENT_UUID IN (SELECT COMPONENT_UUID FROM RC_ITEM_COMPONENT WHERE ITEM_UUID  = #{uuid});
  DELETE FROM RC_COMPONENT WHERE COMPONENT_UUID IN (SELECT COMPONENT_UUID FROM RC_ITEM_COMPONENT WHERE ITEM_UUID  = #{uuid});
  DELETE FROM RC_ITEM_COMPONENT WHERE ITEM_UUID  = #{uuid};

  DELETE FROM RC_ITEM_CREATOR WHERE ITEM_UUID  = #{uuid};
  DELETE FROM RC_ITEM_MATERIAL WHERE ITEM_UUID  = #{uuid};
  DELETE FROM RC_ITEM_RELATED_AUTHORITY WHERE ITEM_UUID  = #{uuid};
  DELETE FROM RC_ITEM_RELATED_RECORD WHERE ITEM_UUID  = #{uuid};

  DELETE FROM RC_ITEM_CON WHERE ITEM_UUID  = #{uuid};
  DELETE FROM RC_ITEM WHERE ITEM_UUID  = #{uuid};

  DELETE FROM RC_AGGREGATION_CREATOR WHERE AGGREGATION_UUID  = #{uuid};
  DELETE FROM RC_AGGREGATION_MATERIAL WHERE AGGREGATION_UUID  = #{uuid};
  DELETE FROM RC_AGGR_RELATED_AUTHORITY WHERE AGGREGATION_UUID  = #{uuid};
  DELETE FROM RC_AGGR_RELATED_RECORD WHERE AGGREGATION_UUID  = #{uuid};

  DELETE FROM RC_AGGREGATION_CON WHERE AGGREGATION_UUID  = #{uuid};
  DELETE FROM RC_AGGREGATION WHERE AGGREGATION_UUID  = #{uuid};

  DELETE FROM RC_RECORD_REFERENCE WHERE AGGREGATION_UUID  = #{uuid} OR ITEM_UUID  = #{uuid};
  commit;
  END
}

mapper.update:
{call
BEGIN
  UPDATE
    RC_AGGREGATION
  SET
    PUBLISHED_STATUS_UUID = #{publishedStatusUuid}
    , UPDATE_DATE = CURRENT_TIMESTAMP
    , UPDATE_UUID = #{updateUuid}
  WHERE
    AGGREGATION_UUID = #{aggregationUuid};
  UPDATE
    RC_ITEM
  SET
    PUBLISHED_STATUS_UUID = #{publishedStatusUuid}
    , UPDATE_DATE = CURRENT_TIMESTAMP
    , UPDATE_UUID = #{updateUuid}
  WHERE
    ITEM_UUID = #{aggregationUuid};
    commit;
END
}

choose.when:
UPDATE
  RC_ITEM
SET
  AGGREGATION_UUID = #{parentUuid}
, UPDATE_DATE = CURRENT_TIMESTAMP
, UPDATE_UUID = #{updateUuid}
WHERE
  ITEM_UUID = #{uuid}

choose.otherwise:
UPDATE
  RC_AGGREGATION
SET
    PARENT_AGGREGATION_UUID = #{parentUuid}
    , UPDATE_DATE = CURRENT_TIMESTAMP
    , UPDATE_UUID = #{updateUuid}
WHERE
  AGGREGATION_UUID = #{uuid}

