mapper.sql:
SELECT Z1.AGGREGATION_UUID AS uuid, Z1.PARENT_AGGREGATION_UUID AS parentUuid, 1 as orderNo, FC_AD_CODE_NM(Z1.TYPE_UUID) AS nodeType, Z1.TITLE AS title, (SELECT LEVEL_NAME FROM RC_LEVEL WHERE LEVEL_UUID = Z1.LEVEL_UUID) as "level", FC_AD_CODE_NM(Z1.PUBLISHED_STATUS_UUID) AS publishedStatus, Z1.AUTHOR AS author, DESCRIPTION_START_DATE AS descriptionStartDate, DESCRIPTION_END_DATE AS descriptionEndDate, DESCRIPTION AS description, NOTES AS notes, ( (SELECT COUNT(*) FROM RC_AGGREGATION WHERE PARENT_AGGREGATION_UUID = Z1.AGGREGATION_UUID) + (SELECT COUNT(*) FROM RC_ITEM WHERE AGGREGATION_UUID = Z1.AGGREGATION_UUID) )as childCnt, Z1.AGGREGATION_UUID AS JOIN_KEY FROM RC_AGGREGATION Z1 WHERE 1=1
AND TITLE LIKE '%' || #{title} || '%'
AND LEVEL_UUID = #{level}
AND TYPE_UUID = #{nodeType}
AND PUBLISHED_STATUS_UUID = #{publishedStatus}
AND AUTHOR LIKE '%' || #{author} || '%'
AND DESCRIPTION_START_DATE >= #{descriptionStartDate}
AND DESCRIPTION_END_DATE <= #{descriptionEndDate}
UNION ALL SELECT ITEM_UUID AS uuid, AGGREGATION_UUID AS parentUuid, 2 AS orderNo, FC_AD_CODE_NM(TYPE_UUID) AS nodeType, TITLE AS title, 'Item' AS "level", FC_AD_CODE_NM(PUBLISHED_STATUS_UUID) AS publishedStatus, AUTHOR AS author, DESCRIPTION_START_DATE AS descriptionStartDate, DESCRIPTION_END_DATE AS descriptionEndDate, DESCRIPTION AS description, NOTES AS notes, 0 AS childCnt, AGGREGATION_UUID AS JOIN_KEY FROM RC_ITEM WHERE 1=1
AND TITLE LIKE '%' || #{title} || '%'
AND TYPE_UUID = #{nodeType}
AND PUBLISHED_STATUS_UUID = #{publishedStatus}
AND AUTHOR LIKE '%' || #{author} || '%'
AND DESCRIPTION_START_DATE >= #{descriptionStartDate}
AND DESCRIPTION_END_DATE <= #{descriptionEndDate}
