mapper.select:
SELECT REPOSITORY_UUID as repositoryUuid, STATUS_UUID as statusUuid, REPOSITORY_CODE as repositoryCode, REPOSITORY_NAME as repositoryName, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM ST_REPOSITORY
STATUS_UUID = #{statusUuid}
AND REPOSITORY_CODE LIKE '%' || #{repositoryCode} || '%'
AND REPOSITORY_NAME LIKE '%' || #{repositoryName} || '%'
AND USE_YN = #{useYn}
ORDER BY REPOSITORY_CODE

SELECT SHELF_UUID as shelfUuid, STATUS_UUID as statusUuid, REPOSITORY_UUID as repositoryUuid, SHELF_CODE as shelfCode, SHELF_NAME as shelfName, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, ( SELECT COUNT(*) FROM ST_LOCATION I WHERE I.SHELF_UUID = A.SHELF_UUID ) maxContainer, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM ST_SHELF A
STATUS_UUID = #{statusUuid}
AND SHELF_CODE = #{shelfCode}
AND SHELF_NAME = #{shelfName}
AND USE_YN = #{useYn}
AND REPOSITORY_UUID = #{repositoryUuid}
ORDER BY SHELF_CODE

SELECT LOCATION_UUID as locationUuid, STATUS_UUID as statusUuid, SHELF_UUID as shelfUuid, ROW_NO as rowNo, COLUMN_NO as columnNo, /*TOTAL_CONTAINER as totalContainer, 실제 배치된 container로 바꾼다*/ DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate, (SELECT COUNT(*) FROM ST_ARRANGE_CONTAINERS_RESULT STACR WHERE STACR.LOCATION_UUID = STL.LOCATION_UUID ) as totalContainer FROM ST_LOCATION STL
STATUS_UUID = #{statusUuid}
AND ROW_NO = #{rowNo}
AND COLUMN_NO = #{columnNo}
AND USE_YN = #{useYn}
AND SHELF_UUID = #{shelfUuid}
