mapper.select:
SELECT S1.ARRANGE_CONTAINERS_RESULT_UUID as arrangeContainersResultUuid, S1.LOCATION_UUID as locationUuid, S1.CONTAINER_UUID as containerUuid, S1.STATUS_UUID as statusUuid, S1.ARRANGED_DATE as arrangedDate, S2.CONTAINER_NAME as containerName, S2.CONTROL_NUMBER as controlNumber, S1.DESCRIPTION as description, S1.NOTES as notes, ( SELECT SUBSTR(SYS_CONNECT_BY_PATH(CONTAINER_NAME,' >> '),4) path FROM ST_CONTAINER S3 WHERE S3.CONTAINER_UUID = S1.CONTAINER_UUID START WITH PARENT_CONTAINER_UUID IS NULL CONNECT BY PRIOR CONTAINER_UUID = PARENT_CONTAINER_UUID ) as containerPath, as insertUuid, S1.INSERT_DATE as insertDate, as updateUuid, S1.UPDATE_DATE as updateDate, SUBSTR(SYS_CONNECT_BY_PATH(S1.ARRANGE_CONTAINERS_RESULT_UUID, '.'), 2 , length(SYS_CONNECT_BY_PATH(S1.ARRANGE_CONTAINERS_RESULT_UUID, '.'))) as orderKey1 FROM ST_ARRANGE_CONTAINERS_RESULT S1 INNER JOIN ST_CONTAINER S2 ON S1.CONTAINER_UUID = S2.CONTAINER_UUID
S1.CONTAINER_UUID = S2.CONTAINER_UUID
AND S1.LOCATION_UUID = #{locationUuid}
AND S1.STATUS_UUID = #{statusUuid}
AND S2.CONTAINER_TYPE_UUID = #{containerType}
AND S2.CONTAINER_NAME LIKE '%' || #{containerName} || '%'
START WITH S2.PARENT_CONTAINER_UUID IS NULL CONNECT BY PRIOR S2.CONTAINER_UUID = S2.PARENT_CONTAINER_UUID ORDER SIBLINGS BY S2.ORDER_KEY

SELECT LOCATION_UUID as locationUuid, STATUS_UUID as statusUuid, SHELF_UUID as shelfUuid, ROW_NO as rowNo, COLUMN_NO as columnNo, /*TOTAL_CONTAINER as totalContainer, 실제 배치된 container로 바꾼다*/ DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate, (SELECT COUNT(*) FROM ST_ARRANGE_CONTAINERS_RESULT STACR WHERE STACR.LOCATION_UUID = STL.LOCATION_UUID ) as totalContainer FROM ST_LOCATION STL
1=1
AND STATUS_UUID = #{statusUuid}
AND ROW_NO = #{rowNo}
AND COLUMN_NO = #{columnNo}
AND USE_YN = #{useYn}
AND SHELF_UUID = #{shelfUuid}
AND FC_AD_CODE_NM(STATUS_UUID) = 'Confirm'
