mapper.select:
SELECT A.AGGREGATION_UUID aggregationUuid, RFID_TAG_REPUBLISH_UUID rfidTagRepublishUuid, B.code, B.title, B."level", B."type", B.publishedStatus, B.author, B.descStrDate, B.descEdDate, repository.REPOSITORY_NAME repositoryName, shelf.SHELF_NAME shelfName, (location.ROW_NO || '행 ' || location.COLUMN_NO || '열') as locationName, PUBLISH_SOURCE_TYPE_UUID publishSourceTypeUuid, REQUEST_DATE requestDate, REPUBLISH_YN republishYn, REPUBLISH_DATE republishDate FROM ST_RFID_TAG_PUBLISH_REQUEST A INNER JOIN ( SELECT ar.AGGREGATION_UUID, ar.CONTAINER_UUID FROM (SELECT AGGREGATION_UUID FROM RC_ITEM GROUP BY AGGREGATION_UUID) item INNER JOIN ST_ARRANGE_RECORDS_RESULT ar ON ar.AGGREGATION_UUID = item.AGGREGATION_UUID ) etc ON etc.AGGREGATION_UUID = A.AGGREGATION_UUID INNER JOIN ST_ARRANGE_CONTAINERS_RESULT acr ON acr.CONTAINER_UUID = etc.CONTAINER_UUID INNER JOIN ST_LOCATION location ON location.LOCATION_UUID = acr.LOCATION_UUID INNER JOIN ST_CONTAINER container ON container.CONTAINER_UUID = etc.CONTAINER_UUID INNER JOIN ST_SHELF shelf ON shelf.SHELF_UUID = location.SHELF_UUID INNER JOIN ST_REPOSITORY repository ON repository.REPOSITORY_UUID = shelf.REPOSITORY_UUID INNER JOIN (SELECT AGGREGATION_UUID as uuid, 1 as orderNo, (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.PUBLISHED_STATUS_UUID) as publishedStatus, AGGREGATION_CODE as code, TITLE as title, (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.TYPE_UUID) as "type", (SELECT LEVEL_NAME FROM RC_LEVEL WHERE LEVEL_UUID = Z1.LEVEL_UUID) as "level", AUTHOR as author, DESCRIPTION_START_DATE as descStrDate, DESCRIPTION_END_DATE as descEdDate, DESCRIPTION as description, NOTES as notes, ((SELECT COUNT(*) FROM RC_AGGREGATION WHERE PARENT_AGGREGATION_UUID = Z1.AGGREGATION_UUID) + (SELECT COUNT(*) FROM RC_ITEM WHERE AGGREGATION_UUID = Z1.AGGREGATION_UUID)) as childCnt FROM RC_AGGREGATION Z1 UNION ALL SELECT ITEM_UUID as uuid, 2 as orderNo, (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.PUBLISHED_STATUS_UUID) as publishedStatus, ITEM_CODE as code, TITLE as title, (SELECT CODE_NAME FROM AD_CODE_DETAIL WHERE CODE_DETAIL_UUID = Z1.TYPE_UUID) as "type", '' as "level", AUTHOR as author, DESCRIPTION_START_DATE as descStrDate, DESCRIPTION_END_DATE as descEdDate, DESCRIPTION as description, NOTES as notes, 0 as childCnt FROM RC_ITEM Z1) B on B.UUID = A.AGGREGATION_UUID
1=1
AND B.code LIKE '%' || #{code} || '%'
AND B.title LIKE '%' || #{title} || '%'
AND repository.REPOSITORY_UUID = #{repositoryUuid}
AND shelf.SHELF_UUID = #{shelfUuid}
AND location.LOCATION_UUID = #{locationUuid}
AND PUBLISH_SOURCE_TYPE_UUID = #{publishSourceTypeUuid}
AND REPUBLISH_YN = #{republishYn}
AND REQUEST_DATE >= TO_DATE(#{requestDateFrom},'yyyy-mm-dd')
AND REQUEST_DATE < TO_DATE(#{requestDateTo},'yyyy-mm-dd')
ORDER BY requestDate desc
