mapper.select:
SELECT Z1.USER_GROUP_USER_UUID as userGroupUserUuid, Z1.USER_GROUP_UUID as userGroupUuid, Z2.USER_GROUP_NAME as userGroupName, Z1.USER_UUID as userUuid, Z1.DESCRIPTION as description, Z1.NOTES as notes, Z1.USE_YN as useYn,
as insertUuid, Z1.INSERT_DATE insertDate,
as updateUuid, Z1.UPDATE_DATE updateDate FROM AC_USER_GROUP_USER Z1, AC_USER_GROUP Z2 WHERE Z1.USER_GROUP_UUID = Z2.USER_GROUP_UUID AND Z1.USER_UUID = #{userUuid}
