mapper.select:
SELECT CLASSIFICATION_SCHEME_UUID as classificationSchemeUuid, STATUS_UUID as statusUuid, CLASSIFICATION_CODE as classificationCode, CLASSIFICATION_NAME as classificationName, CLASSIFICATION_TYPE_UUID as classificationTypeUuid, ORDER_NO as orderNo, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM CL_CLASSIFICATION_SCHEME
STATUS_UUID = #{statusUuid}
AND CLASSIFICATION_TYPE_UUID = #{classificationTypeUuid}
AND CLASSIFICATION_CODE LIKE '%' || #{classificationCode} || '%'
AND CLASSIFICATION_NAME LIKE '%' || #{classificationName} || '%'
AND USE_YN = #{useYn}

SELECT Z1.CLASSIFICATION_SCHEME_UUID as classificationSchemeUuid , Z2.MANAGER_ORGANIZATION as managerOrganization , Z2.MANAGER as manager , Z2.BASED_ON as baseOn , ( SELECT COUNT(USE_YN) FROM CL_CLASS WHERE USE_YN = 'Y' ) as useYes , ( SELECT COUNT(USE_YN) FROM CL_CLASS WHERE USE_YN = 'n' ) as useNo, ( SELECT COUNT(AGGREGATION_ID) FROM CL_CLASSIFIED_RECORDS ) as aggregationCnt, ( SELECT COUNT(ITEM_UUID) FROM CL_CLASSIFIED_RECORDS ) as itemCnt FROM CL_CLASSIFICATION_SCHEME Z1, CL_CLASSIFICATION_SCHEME_CON Z2
Z1.CLASSIFICATION_SCHEME_UUID = #{classificationSchemeUuid}
AND Z1.CLASSIFICATION_SCHEME_UUID = Z2.CLASSIFICATION_SCHEME_UUID
