mapper.select:
SELECT T1.DISPOSAL_FREEZE_DEGREE_UUID AS disposalFreezeDegreeUuid, T1.DISPOSAL_FREEZE_EVENT_UUID AS disposalFreezeEventUuid, T1.STATUS_UUID AS statusUuid, T2.EVENT_CODE AS eventCode, T2.EVENT_NAME AS eventName, T1."DEGREE" AS "degree", T1.KEYWORD AS keyword, T1.END_YN AS endYN,
AS terminatorUuid,
T1.END_DATE AS endDate,
AS insertUuid,
T1.INSERT_DATE AS insertDate,
AS updateUuid,
T1.UPDATE_DATE AS updateDate FROM DF_DISPOSAL_FREEZE_DEGREE T1, DF_DISPOSAL_FREEZE_EVENT T2 WHERE 1=1 AND T1.DISPOSAL_FREEZE_EVENT_UUID = T2.DISPOSAL_FREEZE_EVENT_UUID
AND T1.STATUS_UUID = #{statusUuid}
AND T2.EVENT_CODE = #{eventCode}
AND T2.EVENT_NAME LIKE '%' || #{eventName} || '%'
AND T1.DEGREE = #{degree}
AND T1.END_YN = #{endYN}
ORDER BY T2.EVENT_CODE, T1.DEGREE ASC

SELECT Z1.DISPOSAL_FREEZE_DEGREE_UUID as disposalFreezeEventUuid , (SELECT COUNT(DISPOSAL_FREEZE_DEGREE_UUID) FROM DF_DISPOSAL_FREEZE_DEGREE ) as freezeCnt, (SELECT COUNT(AGGREGATION_ID) FROM DF_DISPOSAL_FREEZE_RESULT ) as aggregationCnt, (SELECT COUNT(ITEM_UUID) FROM DF_DISPOSAL_FREEZE_RESULT ) as itemCnt FROM DF_DISPOSAL_FREEZE_DEGREE Z1, DF_DISPOSAL_FREEZE_RESULT Z2
Z1.DISPOSAL_FREEZE_DEGREE_UUID = #{disposalFreezeEventUuid}
AND Z1.DISPOSAL_FREEZE_DEGREE_UUID = Z2.DISPOSAL_FREEZE_DEGREE_UUID
