SELECT * FROM ( SELECT Z1.ERROR_DATETIME AS errorDatetime, Z1.JISA_CODE AS jisaCode, Z1.BRANCH_CODE AS branchCode, Z1.BRANCH_NAME AS branchName, Z1.BRANCH_TEL AS branchTel, Z1.CORNER_CODE AS cornerCode, Z1.CORNER_NAME AS cornerName, Z1.TERMINAL_NO AS terminalNo, Z1.PLACE_GUBUN AS placeGubun, Z1.OPER_TIME_GUBUN AS operTimeGubun, Z1.RUNTIME AS runtime, Z1.PROD_SERIAL_NO AS prodSerialNo, Z1.TERMINAL_CORP_CODE AS terminalCorpCode, Z1.MODEL_CODE AS modelCode, Z1.PROGRESS_STATUS AS progressStatus, Z1.STEXT_GUBUN AS stextGubun, Z1.TERMINAL_ERROR1 AS terminalError1, Z1.TERMINAL_ERROR1 AS terminalError2, Z1.TOTAL_CLASSIFY_CODE AS totalClassifyCode, Z1.ERROR_TYPE AS errorType, Z1.CASH_PART_STATUS AS cashPartStatus, Z1.CHECK_PART_STATUS AS checkPartStatus, Z1.PORTFOLIO_PART_STATUS AS portfolioPartStatus, Z1.JRNL_PART_STATUS AS jrnlPartStatus, Z1.RTRVL_BOX_STATUS AS rtrvlBoxStatus, Z1.CARD_PART_STATUS AS cardPartStatus, Z1.BNKB_PART_STATUS AS bnkbPartStatus, Z1.GIRO_PART_STATUS AS giroPartStatus, Z1.SUSPEND_STATUS AS suspendStatus, Z1.HW_ERROR_STATUS AS hwErrorStatus, Z1.MAINTENANCE_STATUS AS maintenanceStatus, Z1.VERSION AS version, Z1.ENABLE_DES_BOARD AS enableDesBoard, Z1.ENABLE_IC_CARD AS enableIcCard, Z1.ENABLE_EMV AS enableEmv, Z1.ENABLE_IR AS enableIr, Z1.ENABLE_RF AS enableRf, Z1.ENABLE_FINGERPRINT AS enableFingerprint, Z1.ENCRYPTION_STATUS AS encryptionStatus, Z1.TERMINAL_INFO AS terminalInfo, Z1.ATMS_TERMINAL_NO AS atmsTerminalNo, Z1.CASH_PART_STATUS_50K_WON AS cashPartStatus50kWon, Z1.ATMC_EXCCLC_TERMINAL_ERROR AS atmcExcclcTerminalError, Z1.ATMC_EXCCLC_EXEC_RESULT AS atmcExcclcExecResult, Z1.TERMINAL_VER_INFO AS terminalVerInfo, Z1.CARD_ISSUED_TERMINAL_STATUS AS cardIssuedTerminalStatus, Z1.ID_SCANNER_STATUS AS idScannerStatus, Z1.BIO_SCANNER_STATUS AS bioScannerStatus, Z1.SCRTY_CARD_HIGHEND_ATM_ONLY AS scrtyCardHighendAtmOnly, Z1.CARD_STYLE_OTP AS cardStyleOtp, Z1.S20_GENERAL AS s20General, Z1.S20_FRPY AS s20Frpy, Z1.SLINE_GENERAL AS slineGeneral, Z1.SLINE_FRPY AS slineFrpy, Z1.FOURTUNE_GENERAL AS fourtuneGeneral, Z1.FOURTUNE_FRPY AS fourtuneFrpy, Z1.RCPPAY_BNKB AS rcppayBnkb, Z2.SECURITY_CORP AS securityCorp, Z2.SECURITY_CORP_CONTRACT_NO AS securityCorpContractNo, Z2.BOOTH_CORP AS boothCorp, Z2.BOOTH_TYPE AS boothType, Z2.INTERCOM_NO AS intercomNo, Z2.TERMINAL_LOCATION AS terminalLocation, Z2.COMM_OFFICE AS commOffice, Z2.INTERNET_CLASSIFY AS internetClassify, Z2.MNG_CHANNEL AS mngChannel, Z3.NOTICE_CONTENT AS noticeContent, Z3.CUSTOMER_INFO AS customerInfo, Z3.HANDLE_CONTENT AS handleContent, NVL((SELECT SUBSTR(TX_ID,3,1) FROM ATMS_ERROR_STATUS WHERE JISA_CODE = Z1.JISA_CODE AND BRANCH_CODE = Z1.BRANCH_CODE AND CORNER_CODE = Z1.CORNER_CODE AND TERMINAL_NO = Z1.TERMINAL_NO AND ERROR_DATETIME = Z1.ERROR_DATETIME ),'9') AS txId FROM ATMS_01001110 Z1 , ATMS_TERMINAL_STATUS Z2, ATMS_ERROR_HANDLE_MNG Z3 WHERE Z1.JISA_CODE = Z2.JISA_CODE AND Z1.BRANCH_CODE = Z2.BRANCH_CODE AND Z1.CORNER_CODE = Z2.CORNER_CODE AND Z1.TERMINAL_NO = Z2.TERMINAL_NO AND Z1.JISA_CODE = Z3.JISA_CODE(+) AND Z1.BRANCH_CODE = Z3.BRANCH_CODE(+) AND Z1.CORNER_CODE = Z3.CORNER_CODE(+) AND Z1.TERMINAL_NO = Z3.TERMINAL_NO(+) AND Z1.ERROR_DATETIME = Z3.ERROR_DATETIME(+)
AND Z1.JISA_CODE = #{jisaCode}
AND Z1.BRANCH_CODE = #{branchCode}
AND Z1.CORNER_CODE = #{cornerCode}
AND Z1.TERMINAL_NO = #{terminalNo}
AND Z1.ERROR_TYPE = #{errorType}
AND Z1.ERROR_DATETIME BETWEEN #{startDate} AND #{endDate}
ORDER BY Z1.ERROR_DATETIME DESC )
txId = #{txId}
