mapper.select:
SELECT GENERAL_RECORD_SCHEDULE_UUID as generalRecordScheduleUuid, STATUS_UUID as statusUuid, GRS_CODE as grsCode, GRS_NAME as grsName, RETENTION_PERIOD_UUID as retentionPeriodUuid, DISPOSAL_TYPE_UUID as disposalTypeUuid, BASED_ON as basedOn, TRIGGER_YN as triggerYn, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM RS_GENERAL_RECORD_SCHEDULE
STATUS_UUID = #{statusUuid}
AND RETENTION_PERIOD_UUID = #{retentionPeriodUuid}
AND DISPOSAL_TYPE_UUID = #{disposalTypeUuid}
AND UPPER(GRS_CODE) LIKE '%' || #{grsCode} || '%'
AND UPPER(GRS_NAME) LIKE '%' || #{grsName} || '%'
AND USE_YN = #{useYn}
AND TRIGGER_YN = #{triggerYn}
--ORDER BY
