mapper.select:
SELECT TRIGGER_UUID as triggerUuid, STATUS_UUID as statusUuid, TRIGGER_CODE as triggerCode, TRIGGER_NAME as triggerName, TRIGGER_DATE as triggerDate, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM RS_TRIGGER
STATUS_UUID = #{statusUuid}
AND TRIGGER_CODE = #{triggerCode}
AND TRIGGER_NAME = #{triggerName}
AND USE_YN = #{useYn}
--ORDER BY
