mapper.select:
SELECT REPOSITORY_UUID as repositoryUuid, STATUS_UUID as statusUuid, REPOSITORY_CODE as repositoryCode, REPOSITORY_NAME as repositoryName, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM ST_REPOSITORY
STATUS_UUID = #{statusUuid}
AND REPOSITORY_CODE = #{repositoryCode}
AND REPOSITORY_NAME = #{repositoryName}
AND USE_YN = #{useYn}

SELECT SHELF_UUID as shelfUuid, STATUS_UUID as statusUuid, REPOSITORY_UUID as repositoryUuid, SHELF_CODE as shelfCode, SHELF_NAME as shelfName, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM ST_SHELF
STATUS_UUID = #{statusUuid}
AND SHELF_CODE = #{shelfCode}
AND SHELF_NAME = #{shelfName}
AND USE_YN = #{useYn}

SELECT LOCATION_UUID as locationUuid, STATUS_UUID as statusUuid, SHELF_UUID as shelfUuid, ROW_NO as rowNo, COLUMN_NO as columnNo, TOTAL_CONTAINER as totalContainer, DESCRIPTION as description, NOTES as notes, USE_YN as useYn, as insertUuid, INSERT_DATE as insertDate, as updateUuid, UPDATE_DATE as updateDate FROM ST_LOCATION
STATUS_UUID = #{statusUuid}
AND ROW_NO = #{rowNo}
AND COLUMN_NO = #{columnNo}
AND USE_YN = #{useYn}
