mapper.select:
SELECT Z1.WORKFLOW_RESULT_UUID AS workflowResultUuid, Z1.WORKFLOW_UUID AS workflowUuid, Z1.STATUS_UUID AS statusUuid, Z1.BATCH_ID AS batchId, Z1.WORKFLOW_NAME AS workflowName, Z1.SERVICE_UUID AS serviceUuid, FC_AC_USER_NM(Z1.EXECUTER_UUID) AS executerUuid, Z2.MENU_NAME AS munuName, Z1.START_DATE AS startDate, Z1.END_DATE AS endDate FROM WF_WORKFLOW_RESULT Z1, AD_MENU Z2 WHERE Z1.MENU_UUID = Z2.MENU_UUID
AND Z1.SERVICE_UUID = #{serviceUuid}
AND Z1.STATUS_UUID = #{statusUuid}
AND Z1.BATCH_ID = #{batchId}
AND Z1.WORKFLOW_NAME LIKE '%' || #{workflowName} || '%'
AND Z1.EXECUTER_UUID = #{executer}
AND Z1.MENU_UUID = #{menu}
AND Z1.START_DATE >= TO_DATE(#{startFromDate})
AND Z1.START_DATE < TO_DATE(#{startToDate})+1
AND Z1.END_DATE >= TO_DATE(#{endFromDate})
AND Z1.END_DATE < TO_DATE(#{endToDate})+1
